var setHeightH3 = () => {
  var maxHeight = 0;
  $('.phanam-intro-about-us-3 .block-wrapper .info-wrapper').each(function () {
    var thisH = $(this).height();

    if (thisH > maxHeight) {
      maxHeight = thisH;
    }
  });
  $('.phanam-intro-about-us-3 .block-wrapper .banner-info-wrapper').height(maxHeight);
  $('.phanam-intro-about-us-3 .block-wrapper .info-wrapper').height(maxHeight);
};

function accordion() {
  $('.hotro-1 .question-wrapper .item .title').on('click', function () {
    if ($(this).parents('.item').find('.content').is(':hidden') == true) {
      $('.hotro-1 .question-wrapper .item .title').removeClass('active');
      $(this).addClass('active');
      $('.hotro-1 .question-wrapper .item .content').slideUp();
      $(this).parents('.item').find('.content').slideDown();
    } else {
      $(this).removeClass('active');
      $(this).parents('.item').find('.content').slideUp();
    }
  });
} ///////// MAIN Control /////


