function popup_gioithieu2() {
  $('.gioithieu2-1 map area').on('click', function () {
    let i = $(this).attr('data-popup');
    console.log(i);
    $('.gioithieu2-1 .popup-wrapper .popup-container .popup-content').hide();
    $('.gioithieu2-1 .popup-wrapper .popup-container .popup-content[data-map=' + i + ']').show();
    $('.gioithieu2-1 .popup-wrapper .popup-container .popup-content[data-map=' + i + ']').parents('.popup-wrapper').addClass('active');
  });
  $('.gioithieu2-1 .popup-wrapper .popup-container .popup-close').on('click', function () {
    $(this).parents('.popup-wrapper').removeClass('active');
  });
}

